mapper.select:
select

  distinct


from qrtz_fired_triggers




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

select 

from qrtz_fired_triggers
where SCHED_NAME = #{schedName,jdbcType=VARCHAR}
  and ENTRY_ID = #{entryId,jdbcType=VARCHAR}

mapper.delete:
delete from qrtz_fired_triggers
where SCHED_NAME = #{schedName,jdbcType=VARCHAR}
  and ENTRY_ID = #{entryId,jdbcType=VARCHAR}
